import logo from './logo.svg';
import './App.css';
import React, { useEffect, useState } from "react";
import USAMap from "react-usa-map";
import statesJson from "./states.json";

import Button from "@mui/material/Button";


function App() {
  const [hoveredState, setHoveredState] = useState(null);
  const [color, setColor] = useState({backgroundColor:'orange'});
  const handleStateHover = (event) => {
    setHoveredState(event.target.dataset.name);
  };

  const handleStateLeave = () => {
    setHoveredState(null);
  };

  function handleClick(){
    setColor({backgroundColor:'green'})
  }
  
  return (
    <>
    <div id="website"  style={color} className="container-center-horizontal">
      <h1 id="title">Meal Mapper</h1>
    </div>
      <USAMap
        onMouseOver={handleStateHover}
        onMouseLeave={handleStateLeave}
        onClick={handleClick}
      />
    </>
  );
}

export default App;
